mapper.select:
select up.id permissionId, up.code permissionCode, up.type permissionType, ua.id apiId, ua.name apiName, ua.url apiUrl, ua.method apiMethod, ua.auth_type apiAuthType from iam_permission up inner join iam_permission_role_rel uprr on uprr.permission_id = up.id inner join iam_api ua on up.resource_id = ua.id where up.type = 'SER_API' and up.is_deleted = 0 and ua.status = 1 and up.status = 1 and ua.application_id = #{applicationId} and uprr.role_id in
#{item}
group by up.id
