mapper.select:
select up.id, up.type, up.code
from upms_permission up
inner join upms_permission_role_rel uprr on up.id = uprr.permission_id
where uprr.role_id in

    #{item}

and uprr.type = #{type}

select up.id,
       code
from upms_permission up
         inner join upms_permission_role_rel uprr on uprr.permission_id = up.id
where role_id = #{roleId}
  and uprr.application_id = #{applicationId}
  and uprr.type = #{type}
  and up.is_deleted = 0
